#include <bits/stdc++.h>

using namespace std;

class Solution {
public:
    vector<vector<int>> threeSum(vector<int>& nums) {
        set<pair<int,int>> hasBeenFound;
        vector<vector<int>> solution;
        pair<int, int> temp;

        if(nums.size() < 3) return solution;
        sort(nums.begin(), nums.end());
        int p, q;
        for(int pivot = 1; pivot < nums.size()-1; pivot++){
            p = 0; q = nums.size()-1;
            while(p < pivot && q > pivot){
                if(nums[p] + nums[q] + nums[pivot] == 0){
                    temp.first = nums[p]; temp.second = nums[q];
                    if(hasBeenFound.find(temp) == hasBeenFound.end()){
                        solution.push_back({nums[p], nums[pivot], nums[q]});
                        hasBeenFound.insert(temp);
                    }
                }
                if(nums[p] + nums[q] + nums[pivot] > 0) q--;
                else if(nums[p] + nums[q] + nums[pivot] < 0) p++;
                else {
                    while(p < pivot && nums[p] + nums[q] + nums[pivot] == 0)p++;
                    while(q < pivot && nums[p] + nums[q] + nums[pivot] == 0)q--;
                }
            }
        }

        return solution;
    }
};


